import React, { useState } from "react";
import axios from "axios";

function AddAgent({ token }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleAddAgent = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/agents", 
        { name, email, mobile, password }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message);
      setName(""); setEmail(""); setMobile(""); setPassword("");
    } catch (err) {
      setMessage(err.response?.data?.message || "Error adding agent");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "20px auto" }}>
      <h2>Add Agent</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleAddAgent}>
        <input type="text" placeholder="Name" value={name} onChange={e=>setName(e.target.value)} required style={{ width:"100%", marginBottom:"10px", padding:"8px" }}/>
        <input type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required style={{ width:"100%", marginBottom:"10px", padding:"8px" }}/>
        <input type="text" placeholder="Mobile" value={mobile} onChange={e=>setMobile(e.target.value)} required style={{ width:"100%", marginBottom:"10px", padding:"8px" }}/>
        <input type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} required style={{ width:"100%", marginBottom:"10px", padding:"8px" }}/>
        <button type="submit" style={{ padding:"10px 20px" }}>Add Agent</button>
      </form>
    </div>
  );
}

export default AddAgent;
