import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import AuthPage from "./components/AuthPage";
import UploadCSV from "./components/UploadCSV";
import AgentLists from "./components/AgentLists";
import AddAgent from "./components/AppAgent";
import './style.css';


function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token") || "");

  useEffect(() => {
    if (token && !user) {
      fetchUser();
    }
  }, [token]);

  const fetchUser = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/auth/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setUser(data);
    } catch (err) {
      console.error("Failed to fetch user", err);
      setToken("");
      localStorage.removeItem("token");
    }
  };

  const handleLogout = () => {
    setUser(null);
    setToken("");
    localStorage.removeItem("token");
  };

  return (
    <Router>
      <div>
        {user && (
          <div style={{ padding: "10px", textAlign: "right" }}>
            <span>Welcome, {user.name} </span>
            <button onClick={handleLogout} style={{ marginLeft: "10px", padding: "5px 10px" }}>Logout</button>
          </div>
        )}

        {user && (
          <nav style={{ padding: "10px", textAlign: "center" }}>
            <Link to="/add-agent" style={{ margin: "0 10px" }}>Add Agent</Link>
            <Link to="/upload" style={{ margin: "0 10px" }}>Upload CSV</Link>
            <Link to="/agent-lists" style={{ margin: "0 10px" }}>Agent Lists</Link>
          </nav>
        )}

        <Routes>
          <Route
            path="/"
            element={user ? <Navigate to="/upload" /> : <AuthPage setUser={setUser} setToken={setToken} />}
          />
          <Route
            path="/add-agent"
            element={user ? <AddAgent token={token} /> : <Navigate to="/" />}
          />
          <Route
            path="/upload"
            element={user ? <UploadCSV token={token} /> : <Navigate to="/" />}
          />
          <Route
            path="/agent-lists"
            element={user ? <AgentLists token={token} /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
