import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import AuthPage from "./components/AuthPage";
import UploadCSV from "./components/UploadCSV";
import AgentLists from "./components/AgentLists";

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token") || "");

  useEffect(() => {
    // If token exists, user might already be logged in
    if (token && !user) {
      // Optionally, fetch user info from backend /me endpoint
      fetchUser();
    }
  }, [token]);

  const fetchUser = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/auth/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setUser(data);
    } catch (err) {
      console.error("Failed to fetch user", err);
      setToken("");
      localStorage.removeItem("token");
    }
  };

  const handleLogout = () => {
    setUser(null);
    setToken("");
    localStorage.removeItem("token");
  };

  return (
    <Router>
      <div>
        {user && (
          <div style={{ padding: "10px", textAlign: "right" }}>
            <span>Welcome, {user.name} </span>
            <button onClick={handleLogout} style={{ marginLeft: "10px", padding: "5px 10px" }}>Logout</button>
          </div>
        )}
        <Routes>
          <Route
            path="/"
            element={
              user ? <Navigate to="/upload" /> : <AuthPage setUser={setUser} setToken={setToken} />
            }
          />
          <Route
            path="/upload"
            element={
              user ? <UploadCSV token={token} /> : <Navigate to="/" />
            }
          />
          <Route
            path="/agent/:id"
            element={
              user ? <AgentLists token={token} /> : <Navigate to="/" />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
